    const arialFont = await pdfDoc.embedFont(StandardFonts.Helvetica);
    const arialBoldFont = await pdfDoc.embedFont(StandardFonts.HelveticaBold);

    // const arialFont = await pdfDoc.embedFont(StandardFonts.Helvetica)

