    const page2 = pdfDoc.addPage();
    var n = 0;
    var i = 1;
    const {
      width,
      height
    } = page.getSize();
    const {
      width2,
      height2
    } = page2.getSize();
    const fontSize = 20;
    const fontSizeTitulo = 28;
    var n = 0;
    var n2 = 0;
