    const palavras = texto.split(" ");
    let linhas = [];
    let linhaAtual = "";


  
    for (const palavra of palavras) {
 
      if (linhaAtual.length + (linhaAtual === "" ? 0 : 1) + palavra.length <= maxCaracteresPorLinha) {
        linhaAtual += (linhaAtual === "" ? "" : " ") + palavra;
      } else {
        linhas.push(linhaAtual);
        linhaAtual = palavra;
      }
    }
  
    if (linhaAtual !== "") {
      linhas.push(linhaAtual);
    }
  
    return linhas;
  }
  
