import Navbar from '../NavBar/NavBarSGI';
import React, { useEffect, useState } from "react";
import Conexao from '../../Config/conexao';
import axios from 'axios';
import moment from 'moment';
import './CadastroDeResponsaveis.css';


export default function CadastroDeResponsaveis(){

    const[setor,setSetor]=useState();
    const[nome,setNome]=useState();
    const[email,setEmail]=useState();
    const[telefone,setTelefone]=useState();

    function Confirmacao(){
        alert ('Dados Salvos com sucesso')

    };

      //BANCO DE DADOS

  async function add() {
  
    const obj = { setor, nome, email, telefone};
   
    const res = await axios.post(Conexao.api+ 'InsertCadastroResponsaveisWEB.php', obj);
    if (res.data.success === true) {
        Confirmacao();
        Limpar();
    }   
      
  };

  

     function Limpar(){
        setSetor('');
        setEmail('');
        setNome('');
        setTelefone('');
         };

    return <body>

<Navbar />
      <br/>
      <div className='titulolaboratorio container-fluid '>
        <h1 className='texttitulo'>Cadastro de Responsáveis</h1>
      </div> 

      <br/><br/><br/>

      <div className='d-flex align-items-center justify-content-center p-3 container mt-5'>

      <div className='col text-center me-2'>
            <h6> Setor</h6>
            <select name='Origem' id='Origem' className=' form-select align-items-center text-start colorInput ' onChange={(e) => setSetor(e.target.value)} value={setor} >                      
             <option selected value="...">Selecione...</option> 
             <option value="QSSMA">QSSMA</option> 
             <option value="RH">RH</option>
             <option value="Geotecnia">Geotecnia</option>
             <option value="Comercial">Comercial</option>
             <option value="Laboratório">Laboratório</option>  
             <option value="Manutençao">Manutençao</option> 
             <option value="Pesquisa e Mineral">Pesquisa e Mineral</option> 
             <option value="Compras">Compras</option> 
            
          </select> 
        </div>

        <div className='col-3 text-center me-2'>
            <h6> Nome</h6>
            <input className='form-control text-center colorInput p-1' onChange={(e) => setNome(e.target.value)} value={nome}></input>
        </div>
        <div className='col text-center me-2'>
            <h6> E-mail</h6>
            <input type="email" className='form-control text-center colorInput p-1 ' onChange={(e) => setEmail(e.target.value)} value={email}></input>
        </div>
        <div className='col text-center '>
            <h6> Telefone</h6>
            <input type="tel" className='bordaa text-center colorInput p-1' onChange={(e) => setTelefone(e.target.value)} value={telefone}></input>
        </div>

      </div>

      <br/><br/>
      <div class="d-grid gap-2 d-md-flex justify-content-md-end bbb">
        <button class="btnlogin  me-md-2  " onClick={(e) =>  add(e)  }> Salvar</button>
      </div>



    </body>
};
