  const[previsaoAcao3,setPrevisaoAcao3]=useState();
  const[execucaoAcao1,setExecucaoAcao1]=useState([]);
  const[execucaoAcao2,setExecucaoAcao2]=useState();
  const[execucaoAcao3,setExecucaoAcao3]=useState();
