  const [imagem, setImagem] = useState();
  const [imagemPreview, setImagemPreview] = useState();
  const [imagem2, setImagem2] = useState();
  const [imagemPreview2, setImagemPreview2] = useState();
  const [imagem3, setImagem3] = useState();
  const [imagemPreview3, setImagemPreview3] = useState();
  const [imagem4, setImagem4] = useState();
  const [imagemPreview4, setImagemPreview4] = useState();
  const [imagem5, setImagem5] = useState();
  const [imagemPreview5, setImagemPreview5] = useState();

  const [numeroItem, setNumeroItem] = useState(1);

 
//cols={30}
  

const listaDeAcoes = acoes.map((f,i)=> <div key={i} class="row d-flex align-items-start justify-content-center ">
        <div class="col-1">{f.item1} </div>
       {/*  <div class="col-4 ">{f.acao1}</div> */}
        <div class="col-4"> <textarea rows={3} cols={50} readOnly className=" bordaa  text-center colorInput resize">{f.acao1}</textarea></div>
        <div class="col-2">{f.responsavelAcao1}</div>
        <div class="col-2">{moment(f.previsaoAcao1).format('DD/MM/YYYY')} </div>
        <div class="col-1">{moment(f.execucaoAcao1).format('DD/MM/YYYY')}</div>
        <div class="col-2">  <button  onClick={() => ClickDelete(f.id) } className="far fa-trash-alt icone-acao red botaao"></button></div>
        </div>)


  const listaVerificacao = verificaEficacia.map((v,i)=> <div key={i} class="row d-flex align-items-center justify-content-center ">
      <div class="col-2 "> <textarea className=" bordaa  text-center colorInput resize">{v.verAcao1}</textarea>  </div>
      <div class="col-2"> <textarea className=" bordaa  text-center colorInput resize">{v.verResponsavelAcao1}</textarea> </div>
      <div class="col-2">{moment(v.verPrevisaoAcao1).format('DD/MM/YYYY')}</div>
      <div class="col-1"> {moment(v.verExecucaoAcao1).format('DD/MM/YYYY')} </div>
      <div class="col-2"> {v.verResultado1}  </div>
      <div class="col-2"> <textarea className=" bordaa  text-center colorInput resize">{v.verComentario1}</textarea> </div> 
      
       
      {setorDoCargo === 'SGI'?
      <div class="col-1">  <button  onClick={() => ClickDeleteVerifica(v.id) } className="far fa-trash-alt icone-acao red botaao"></button></div> 
      :null}
      
      {setorDoCargo !== 'SGI'?
      <div class="col-1"> </div> 
      :null}

      </div>)

     
  function Confirmacao() {
    alert('Solicitação aberta com Sucesso!')    
      };

  

  //BANCO DE DADOS

 /*  async function add() {
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString('pt-BR');
    const obj = { origem, cliente, setorIdentificou, setorOrigem, descrever, documentoDoCliente, responsavel1, responsavel2, reincidente, descricaoproblema, evidencia1, evidencia2, origemSetor, dataAtual: formattedDate, imagem: imagemPreview };
    
    const res = await axios.post(Conexao.api+ 'InsertRegistroDeNaoConformidadeWEB.php', obj);
  //  const res = await axios.post(  + 'InsertAberturaOSManutencao.php', obj);
    if (res.data.success === true) {
      Confirmacao()
      getProtocolo();
    }
  
     
    };


